(function(){tinymce.PluginManager.requireLangPack('hcard');tinymce.create('tinymce.plugins.hCardPlugin',{init:function(ed,url){ed.addCommand('hcardDialog',function(){ed.windowManager.open({file:url+'/dialog.htm',width:435+parseInt(ed.getLang('hcard.delta_width',0)),height:420+parseInt(ed.getLang('hcard.delta_height',0)),inline:1},{plugin_url:url,})});ed.addButton('hcard',{title:'hcard.desc',cmd:'hcardDialog',image:url+'/img/hcard_button.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('hcard',(tinymce.DOM.hasClass(n.parentNode.parentNode,'vcard')||tinymce.DOM.hasClass(n.parentNode,'vcard')||tinymce.DOM.hasClass(n,'vcard')))})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'hCard plugin',author:'Alex Willemsma',authorurl:'http://www.undergroundwebdesigns.com',infourl:'http://www.undergroundwebdesigns.com',version:"0.1"}}});tinymce.PluginManager.add('hcard',tinymce.plugins.hCardPlugin)})();